import './App.css';
import React, { useEffect, useState } from "react";
import Axios from 'axios';
import axios from 'axios';


function App() {
  const [PRODNR,setPRODNR]=useState("");
  const[PRODNAME,setPRODNAME]=useState("");
  const[ PRODTYPE,setPRODTYPE]=useState("");
  const[QUANTITY,setQUANTITY]=useState("");
  const[datalist,setdatalist]=useState([]);
  const Insert = () =>{
  Axios.post("http://localhost:3001/insertrow",{
      PRODNR:parseInt(PRODNR),
      PRODNAME:PRODNAME ,
      PRODTYPE: PRODTYPE ,
      QUANTITY : parseInt(QUANTITY)

    }).then(() => { alert("succesful insert");}
    );
  }
   useEffect(()=>{
     Axios.get("http://localhost:3001/get").then((response)=>{
       setdatalist(response.data);
     })
   },[]) ;


    return (
    <div className="App">

        <h1>CRUD NEW</h1>
        <div className="form">
          <lable class="input-box">
           PRODNR	
          <input type="text" name="PRODNR	" onChange={(e)=>{setPRODNR(e.target.value)}}></input></lable>
          <lable className="input-box">
           PRODNAME
          <input type="text" name="PRODNAME" onChange={(e)=>{setPRODNAME(e.target.value)}}></input></lable>
          <lable className="input-box">
           PRODTYPE
          <input type="text" name="PRODTYPE" onChange={(e)=>{setPRODTYPE(e.target.value)}}></input>
          </lable>
          <lable className="input-box">
           AVAILABLE QUANTITY
          <input type="text" name="	AVAILABLE QUANTITY" onChange={(e)=>{setQUANTITY(e.target.value)}}></input>
          </lable>
          <button onClick={Insert}>CREATE</button>

        </div>

        <div className="table-display">
          <table>
            <thead>
            <tr> 
            <th className="table-head" id="fit">PRODNR</th>
            <th className="table-head">PRODNAME</th>
            <th className="table-head">PRODTYPE</th>
            <th className="table-head" >AVAILABLE QUANTITY</th>


          	</tr>
            </thead>
            <tbody>
              {
              datalist.map((data,i) =>

              <tr>
              <td>{data.PRODNR}</td>
              <td>{data.PRODNAME}</td>
              <td>{data.PRODTYPE}</td>
              <td>{data.AVAILABLEQUANTITY}</td>

              </tr> )
            }

            </tbody>

            </table>

        </div>
    </div>
  );
}

export default App;
