import React from "react";
import { useNavigate, useLocation } from "react-router";
import {useState} from 'react'



function Assignment(){
  const location=useLocation();
  let corid =location.state.corid;
  let assignment=[{id:1,name:"python data",Due:'10/12/2021 23:55',desc:"some descccc"}];
  let [aid,setaid]=useState();
  const navigate= useNavigate();
  const handleaid=()=>{
    navigate('/studentmain/content',{state:{aid:aid}});

  };

    return (<div class="ann">
    <p>{corid}</p>
    <table>
        <tr >
            <th class="table-head"> ASSIGNMENT </th>
            <th class="table-head"> NAME </th>
            <th class="table-head"> DUE DATE </th>

        </tr>
          {assignment.map((item =>
          
            <tr><td>{assignment.indexOf(item)+1}</td>
            <td> <button 
            onClick={
                ()=>{setaid(item.id);
                    handleaid();}
            
            }
            >{item.name} </button></td>
                <td>{item.Due}</td>
          </tr> ))}
        </table>
        <p>{aid}</p>
    
    </div>
    )
;
}
export default Assignment;
