import React from "react";
import { useNavigate } from "react-router";
import {useState} from 'react'



function Assignment(){
  let assignment=[{id:1,name:"python data",Due:'10/12/2021 23:55',desc:"some descccc"}];
  let [aid,setaid]=useState();
  const navigate= useNavigate();
  const handleaid=()=>{
    navigate('/studentmain/content',{state:{aid:this.aid}});

  };

    return (<div class="ann">
    
    <table>
        <tr >
            <th class="table-head"> ASSIGNMENT </th>
            <th class="table-head"> NAME </th>
            <th class="table-head"> DUE DATE </th>

        </tr>
          {assignment.map((item =>
          
            <tr><td>{assignment.indexOf(item)+1}</td>
            <td> <button onClick={()=>setaid(item.id).then(()=>{handleaid})}>{item.name} </button></td>
                <td>{item.Due}</td>
          </tr> ))}
        </table>
    
    </div>
    )
;
}
export default Assignment;
