import React from "react";
import { useLocation } from "react-router-dom";
import Display from './Display';
import './Studentmain.css';
import Quizes from "./Quizes";
/*
       {courses.map((item =>
       <tr><td>{item.corid}</td>
           <td>{item.name}</td>
       </tr>
                  ))}
     </table> */

import {Route ,Routes, useNavigate} from 'react-router-dom';
import Announcements from "./Announcements";
import { useEffect, useState } from "react";
function Studentmain (){
    let [id,setid]=useState();
    let [name,setname]=useState();
    let [corid,setcorid]=useState() ;


    let courses =[{corid:1,name:'Calculus'},{corid:2,name:'Intro to Programming'},{corid:3,name:'Computing'},{corid:4,name:'Pak Studies'},{corid:5,name:'Islamiat'}]
    const location = useLocation();
    if (location.state != null){
        id = location.state && location.state.id;
        
            localStorage.setItem("id", String(id));
          
           
    }
    else{
        
            id=localStorage.getItem("id");
     }
     
     const coridchanger =()=>{
        

     }
  
return(
    <div>
      <nav className="NAB-BAR"> 
        <ul>
        {courses.map((item =>
           <li><button onClick={()=>{setcorid(item.corid)}} id="name"> {item.name}</button></li>       
           
           ))}
        </ul>
      </nav>
      
      

       <div class="vertical-menu">
      <a href="" class="active">{id}</a>
      <a href="/Studentmain/announcements">ANNOUNCEMENTS</a>
      <a href="/Studentmain/quizes">QUIZES</a>
      <a href="/Studentmain/assignment">ASSIGNMENT</a>
      <a href="/Studentmain/report">TERM REPORT</a>
     </div>
    
       <div class="compon">
        <Routes>
        <Route path='/announcements' element={<Announcements/>}/>
        <Route path='/quizes' element={<Quizes/>}/>
        <Route path='/assignment' element={<Announcements/>}/>
        <Route path='/report' element={<Announcements/>}/>



        
        </Routes>
        </div>
        
    
    </div>) ;

};
export default Studentmain ;
