import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import {useState} from 'react';
import './Assignment.css';



function Assignment(props){
  const location=useLocation();
  let id;
  let corid =props.corid;
  if (props.id != null){
    id = props.id;
    localStorage.setItem("id", String(id));
    }
else{
    
        id=localStorage.getItem("id");
 }
 
  let assignment=[{id:1, corid:4 ,name:"python data",Due:'10/12/2021 23:55',desc:"some descccc"},{id:2,corid:4,name:"LATEX",Due:'10/12/2021 23:55',desc:"some descccc"}, {id:1 ,corid:5,name:"Database json/xml/subqueriess",Due:'10/12/2021 23:55',desc:"some descccc"}];
  let [aid,setaid]=useState();
  const navigate= useNavigate();
  let it ;
  const handleaid=()=>{
    setaid(it);
    navigate('/studentmain/content',{state:{aid:aid , id:id}});

  };

    return (<div class="ann">
    <p>{corid}</p>
    <table>
        <tr >
            <th class="table-head"> ASSIGNMENT </th>
            <th class="table-head"> NAME </th>
            <th class="table-head"> DUE DATE </th>

        </tr>
          {assignment.map((item =>
            {if(item.corid=corid) { 
            <tr><td>{assignment.indexOf(item)+1}</td>
            <td> <button 
            onClick={
                ()=>{it=item.id;
                    handleaid();}
            
            }
            >{item.name} </button></td>
                <td>{item.Due}</td>
          </tr> }}))}
        </table>
        <p>{aid}</p>
        <p>{corid}</p>
    </div>
    )
;
}
export default Assignment;
